import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface CompanyDrive {
  id: number;
  company: string;
  skillsRequired: string;
  location: string;
  jobTitle: string;
}

@Component({
  selector: 'app-drive',
  templateUrl: './drive.component.html',
  styleUrls: ['./drive.component.css']
})
export class DriveComponent implements OnInit {

  companyDrives: CompanyDrive[] = [];
  selectedFile: File | null = null;

  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.fetchCompanyDrives();
  }

  fetchCompanyDrives() {
    this.http.get<CompanyDrive[]>('http://localhost:8080/recruiter/getAllCompany')
      .subscribe((data) => {
        this.companyDrives = data;
      });
  }

  onFileSelected(event: Event) {
    const fileInput = event.target as HTMLInputElement;
    if (fileInput.files && fileInput.files.length > 0) {
      this.selectedFile = fileInput.files[0];
    }
  }

  applyToCompany(drive: any) {
    if (this.selectedFile) {
      const formData = new FormData();
      formData.append('file', this.selectedFile); // File attached

      // POST request to apply with file attached
      alert('Application Submitted successful');
      this.http.post(`http://localhost:8080/companydrives/apply/${drive.id}`, formData)
        .subscribe(response => {

          console.log('Application successful', response);
        }, error => {
          console.error('Error in application', error);
        });
    } else {
      alert('Please select a Resume before applying.');
    }
  }

  rejectCompany(drive: any): void {
    alert(`Rejected company: ${drive.company}`);
    this.http.post(`http://localhost:8080/companydrives/reject/${drive.company}`, {})
      .subscribe(response => {

        console.log(`Rejected company: ${drive.company}`);
      }, error => {
        console.error('Error rejecting company', error);
      });
  }
}

<!-- Adjusted Apply and Reject buttons with modern styling -->
<div style="font-family: Arial, sans-serif; padding: 20px; background-color: #f9f9f9;">
  <h2 style="text-align: center; color: #4CAF50; margin-bottom: 20px;">Company Drive Information</h2>

  <table style="border-collapse: collapse; width: 100%; box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1); background-color: #ffffff; border-radius: 10px; overflow: hidden;">
    <thead>
      <tr style="background-color: #4CAF50; color: white;">
        <th style="padding: 12px; text-align: left;">Company Name</th>
        <th style="padding: 12px; text-align: left;">Skills Required</th>
        <th style="padding: 12px; text-align: left;">Job Title</th>
        <th style="padding: 12px; text-align: left;">Actions (Apply/Reject)</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let drive of companyDrives" style="border-bottom: 1px solid #ddd;">
        <td style="padding: 12px; color: #333;">{{ drive.company }}</td>
        <td style="padding: 12px; color: #333;">{{ drive.skillsRequired }}</td>
        <td style="padding: 12px; color: #333;">{{ drive.jobTitle }}</td>
        <td style="padding: 12px;">
          <!-- File input for resume upload -->
          <input type="file" (change)="onFileSelected($event)" style="margin-right: 10px; padding: 5px; border: 1px solid #ccc; border-radius: 5px;" />

          <!-- Button to apply to a company -->
          <button style="background-color: #4CAF50; color: white; border: none; padding: 8px 12px; border-radius: 5px; cursor: pointer; margin-right: 5px; transition: background-color 0.3s;"
                  (mouseover)="this.style.backgroundColor='#45a049'" 
                  (mouseout)="this.style.backgroundColor='#4CAF50'" 
                  (click)="applyToCompany(drive)">
            Apply
          </button>

          <!-- Button to reject a company -->
          <button style="background-color: #f44336; color: white; border: none; padding: 8px 12px; border-radius: 5px; cursor: pointer; transition: background-color 0.3s;"
                  (mouseover)="this.style.backgroundColor='#e53935'" 
                  (mouseout)="this.style.backgroundColor='#f44336'" 
                  (click)="rejectCompany(drive)">
            Reject
          </button>
        </td>
      </tr>
    </tbody>
  </table>

  <br>

  <!-- Button to navigate to the view of applied companies -->
  <div style="text-align: center;">
    <button style="background-color: #2196F3; color: white; border: none; padding: 12px 20px; border-radius: 5px; cursor: pointer; transition: background-color 0.3s; box-shadow: 0 5px 10px rgba(0, 0, 0, 0.1);"
            (mouseover)="this.style.backgroundColor='#1e88e5'" 
            (mouseout)="this.style.backgroundColor='#2196F3'" 
            [routerLink]="['/viewdrive']">
      View Applied
    </button>
  </div>
</div>
