import { Component, OnInit, EventEmitter, Output } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../auth-service.service';

@Component({
  selector: 'app-student-dashboard',
  templateUrl: './student-dashboard.component.html',
  styleUrls: ['./student-dashboard.component.css']
})
export class StudentDashboardComponent implements OnInit {
  username: string = ''; // To store the username
  @Output() logout = new EventEmitter<void>();

  constructor(private router: Router, private authService: AuthService) {}

  ngOnInit() {
    const username = this.authService.getUser(); // Retrieve user data
    if (username) {
      this.username = username.username; // Get the username
    } else {
      this.router.navigate(['/student-dashboard']); // Redirect if user not found
    }
  }

  onLogout() {
    this.authService.clearUser(); // Clear user data
    this.router.navigate(['/']); // Redirect to login page
  }
}
<h3>Welcome to Student Dashboard {{ username }} !</h3>
<button (click)="onLogout()">Logout</button>

<form [formGroup]="studentForm" (ngSubmit)="onSubmit()">
  <div>
    <label for="name">Name</label>
    <input id="name" formControlName="name" placeholder="Enter your name" />
    <div *ngIf="studentForm.get('name')?.invalid && studentForm.get('name')?.touched">
      Name is required
    </div>
  </div>

  <div>
    <label for="email">Email</label>
    <input id="email" formControlName="email" placeholder="Enter your email" />
    <div *ngIf="studentForm.get('email')?.invalid && studentForm.get('email')?.touched">
      Valid email is required
    </div>
  </div>

  <div>
    <label for="education">Education</label>
    <input id="education" formControlName="education" placeholder="Enter your education" />
    <div *ngIf="studentForm.get('education')?.invalid && studentForm.get('education')?.touched">
      Education is required
    </div>
  </div>

  <div>
    <label for="skills">Skills</label>
    <input id="skills" formControlName="skills" placeholder="Enter your skills" />
  </div>

  <div>
    <label for="experience">Experience</label>
    <input id="experience" formControlName="experience" placeholder="Enter your experience" />
  </div>

  <button type="submit" [disabled]="studentForm.invalid">Submit</button>
</form>
