import { Component } from '@angular/core';

@Component({
  selector: 'app-welcome',
  templateUrl: './welcome.component.html',
  styleUrls: ['./welcome.component.css']
})
export class WelcomeComponent {
  currentComponent: string = 'welcome'; // Initially show the welcome page

  constructor(private router: Router) {} // Inject Router

  // Navigate to Student Login
  goToStudentLogin() {
    this.router.navigate(['/student-login']);
  }

  // Navigate to Recruiter Login
  goToRecruiterLogin() {
    this.router.navigate(['/recruiter-login']);
  }

  // Function to show login form
  showLogin() {
    this.currentComponent = 'login';
  }

  // Function to show register form
  showRegister() {
    this.currentComponent = 'register';
  }

  // Function to handle login success and switch to student dashboard
  onLoginSuccess() {
    this.currentComponent = 'isLoggedIn';
  }

  // Function to handle logout and switch back to welcome page
  showWelcomePage() {
    this.currentComponent = 'welcome'; // Redirect back to welcome
  }
}
