import { Component, EventEmitter, Output } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { AuthService } from '../auth-service.service';

@Component({
  selector: 'app-recruiter-login',
  templateUrl: './recruiter-login.component.html',
  styleUrls: ['./recruiter-login.component.css']
})
export class RecruiterLoginComponent {
  email: string = '';
  password: string = '';
  errorMessage: string = '';

  constructor(
    private http: HttpClient,
    private router: Router,
    private authService: AuthService
  ) { }

  @Output() loginSuccess = new EventEmitter<void>();
  isLoggedIn: boolean = false;


  onLoginSuccess() {
    this.isLoggedIn = true;
  }


  onSubmit() {
    console.log('Username:', this.email);
    console.log('Password:', this.password);
    const loginData = {
      email: this.email,
      password: this.password
    };

    // Perform login HTTP request
    this.http.post('http://localhost:8080/recruiter/login', loginData).subscribe(
      response => {
        console.log('Login successful', response);
        this.authService.setUser(response);
        this.router.navigate(['/recruiter-dashboard']); // Example redirect on success
      },
      error => {
        this.errorMessage = error.error.message;
      }
    );
  }

}
