  <input [(ngModel)]="recruiter.name" name="name" placeholder="Name" required />
  <input [(ngModel)]="recruiter.company" name="company" placeholder="Company" required />
  <input [(ngModel)]="recruiter.email" name="email" type="email" placeholder="Email" required />
  <input [(ngModel)]="recruiter.phone" name="phone" placeholder="Phone" required />
  <input [(ngModel)]="recruiter.position" name="position" placeholder="Position" required />
  <button type="submit">Add Recruiter</button>
</form>
