import { HttpClient } from '@angular/common/http';
import { Component, EventEmitter, Output } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-student-login',
  templateUrl: './student-login.component.html',
  styleUrls: ['./student-login.component.css']
})
export class StudentLoginComponent {
  username: string = '';
    password: string = '';
    errorMessage: string = '';

    constructor(private http: HttpClient, private router: Router
  ) {}
  currentComponent: string = '';

  @Output() loginSuccess = new EventEmitter<void>();

  isLoggedIn: boolean = false;

    onLoginSuccess() {
      this.isLoggedIn = true;
    }

    onSubmit() {
      console.log('Username:', this.username);
      console.log('Password:', this.password);

      const loginData = {
        username: this.username,
        password: this.password
      };

      // Ensure Content-Type is set
      this.http.post<any>('http://localhost:8080/naukri/login', loginData, {
        headers: { 'Content-Type': 'application/json' }
      }).subscribe(
        (response: any) => {
          console.log('Login successful', response);
          alert('Login successful');
          this.loginSuccess.emit();
          this.router.navigate(['/student-dashboard']); 
        },
        (error) => {
          console.error('Login failed', error);
          this.errorMessage = 'Invalid username or password';
        }
      );
    }
}
<p>Student <b>Login</b> Page!</p>

<form (ngSubmit)="onSubmit()" #studentForm="ngForm">
  <div>
    <label for="emailOrMobile">Email or Mobile Number</label>
    <input
      type="text"
      id="emailOrMobile"
      name="emailOrMobile"
      required
      [(ngModel)]="emailOrMobile"
      #emailOrMobileInput="ngModel"
    />
    <span *ngIf="emailOrMobileInput.invalid && emailOrMobileInput.touched">
      Email or Mobile Number is required.
    </span>
  </div>

  <div>
    <label for="password">Password</label>
    <input
      type="password"
      id="password"
      name="password"
      required
      minlength="4"
      [(ngModel)]="password"
      #passwordInput="ngModel"
    />
    <span *ngIf="passwordInput.invalid && passwordInput.touched">
      Password is required (at least 4 characters).
    </span>
  </div>

  <button type="submit" [disabled]="studentForm.invalid">Submit</button>

  <div *ngIf="errorMessage" style="color: red;">
    {{ errorMessage }}
  </div>
</form>
