import { Component } from '@angular/core';
import { RecruiterServiceService } from '../recruiter-service.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-recruiter-login',
  templateUrl: './recruiter-login.component.html',
  styleUrls: ['./recruiter-login.component.css']
})
export class RecruiterLoginComponent {
  username: string = '';
  mobileNumber: string = '';
  email: string = '';
  errorMessage: string = '';
  successMessage: string = '';

  constructor(private recruiterService: RecruiterServiceService,private router:Router) {}
  Login() {
    this.router.navigate(['/student-login']); // Redirect to login page
  }

  onSubmit() {
    const recruiterData = {
      username: this.username,
      mobileNumber: this.mobileNumber,
      email: this.email
    };

    this.recruiterService.registerRecruiter(recruiterData).subscribe(
      (response: any) => {
        console.log('Data successfully sent to backend:', response);

        // Assuming the password is part of the response
        this.successMessage = 'Registration successful! Your password is: ' + response.password;
        this.errorMessage = '';
      },
      error => {
        if (error.status === 409) {
          if (error.error === 'Error: Email already exists') {
            console.error('Error: Email already exists!');
            this.errorMessage = 'Email already exists!';
          } else if (error.error === 'Error: Mobile already exists') {
            console.error('Error: Mobile number already exists!');
            this.errorMessage = 'Mobile number already exists!';
          }
        } else {
          console.error('Error occurred while submitting form:', error);
          this.errorMessage = 'An error occurred. Please try again.';
        }
      }
    );
  }
}
<p>Recruiter <b>Login</b> Page!</p>
<form (ngSubmit)="onSubmit()" #recruiterForm="ngForm">
  <label for="username">Username</label>
  <input
    type="text"
    [(ngModel)]="username"
    name="username"
    id="username"
    required
    minlength="3"
    #usernameInput="ngModel"
  />
  <div *ngIf="usernameInput.invalid && usernameInput.touched">
    <small *ngIf="usernameInput.errors?.['required']">Username is required.</small>
    <small *ngIf="usernameInput.errors?.['minlength']">Username must be at least 3 characters long.</small>
  </div>
  <br /><br />
  <label for="mobileNumber">Mobile</label>
  <input
    type="text"
    [(ngModel)]="mobileNumber"
    name="mobileNumber"
    id="mobileNumber"
    required
    pattern="^[0-9]{10}$"
    #mobileInput="ngModel"
  />
  <div *ngIf="mobileInput.invalid && mobileInput.touched">
    <small *ngIf="mobileInput.errors?.['required']">Mobile number is required.</small>
    <small *ngIf="mobileInput.errors?.['pattern']">Mobile number must be exactly 10 digits.</small>
  </div>
  <br /><br />
  <label for="email">Email</label>
  <input
    type="email"
    [(ngModel)]="email"
    name="email"
    id="email"
    required
    email
    #emailInput="ngModel"
  />
  <div *ngIf="emailInput.invalid && emailInput.touched">
    <small *ngIf="emailInput.errors?.['required']">Email is required.</small>
    <small *ngIf="emailInput.errors?.['email']">Please enter a valid email address.</small>
  </div>
  <br /><br />
  <button type="submit" [disabled]="recruiterForm.invalid">Register</button>

