import { Component } from '@angular/core';
import { RecruiterServiceService } from '../recruiter-service.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-recruiter-login',
  templateUrl: './recruiter-login.component.html',
  styleUrls: ['./recruiter-login.component.css']
})
export class RecruiterLoginComponent {
  username: string = '';
  mobileNumber: string = '';
  email: string = '';
  errorMessage: string = '';
  successMessage: string = '';

  constructor(private recruiterService: RecruiterServiceService,private router:Router) {}
  Login() {
    this.router.navigate(['/student-login']); // Redirect to login page
  }

  onSubmit() {
    const recruiterData = {
      username: this.username,
      mobileNumber: this.mobileNumber,
      email: this.email
    };

    this.recruiterService.registerRecruiter(recruiterData).subscribe(
      (response: any) => {
        console.log('Data successfully sent to backend:', response);

        // Assuming the password is part of the response
        this.successMessage = 'Registration successful! Your password is: ' + response.password;
        this.errorMessage = '';
      },
      error => {
        if (error.status === 409) {
          if (error.error === 'Error: Email already exists') {
            console.error('Error: Email already exists!');
            this.errorMessage = 'Email already exists!';
          } else if (error.error === 'Error: Mobile already exists') {
            console.error('Error: Mobile number already exists!');
            this.errorMessage = 'Mobile number already exists!';
          }
        } else {
          console.error('Error occurred while submitting form:', error);
          this.errorMessage = 'An error occurred. Please try again.';
        }
      }
    );
  }
}
<p>Recruiter <b>Login</b> Page!</p>

<p>Student <b>Login</b> Page!</p>

<form (ngSubmit)="onSubmit()" #studentForm="ngForm">
  <div>
    <label for="emailOrMobile">Email or Mobile Number</label>
    <input
      type="text"
      id="emailOrMobile"
      name="emailOrMobile"
      required
      [(ngModel)]="emailOrMobile"
      #emailOrMobileInput="ngModel"
    />
    <span *ngIf="emailOrMobileInput.invalid && emailOrMobileInput.touched">
      Email or Mobile Number is required.
    </span>
  </div>

  <div>
    <label for="password">Password</label>
    <input
      type="password"
      id="password"
      name="password"
      required
      minlength="4"
      [(ngModel)]="password"
      #passwordInput="ngModel"
    />
    <span *ngIf="passwordInput.invalid && passwordInput.touched">
      Password is required (at least 4 characters).
    </span>
  </div>

  <button type="submit" [disabled]="studentForm.invalid">Submit</button>


  <div *ngIf="errorMessage" style="color: red;">
    {{ errorMessage }}
  </div>

<!-- <button (click)="Register()" style="margin: 10px;">Register</button> -->
<button class="btn-space" [routerLink]="['/app-student-reg']">Register</button>
</form>
