import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { CompanyDriveService } from '../services/company-drive.service';

interface CompanyDrive {
  id: number;
  company: string;
  skillsRequired: string;
  location: string;
  jobTitle: string;
}

@Component({
  selector: 'app-drive',
  templateUrl: './drive.component.html',
  styleUrls: ['./drive.component.css']
})
export class DriveComponent implements OnInit {
  companyDrives: CompanyDrive[] = [];

  constructor(private http: HttpClient) {}

  ngOnInit() {
    this.fetchCompanyDrives();
  }

  fetchCompanyDrives() {
    this.http.get<CompanyDrive[]>('http://localhost:8080/recruiter/getAllCompany')
      .subscribe((data) => {
        this.companyDrives = data;
      });
  }

}
<h2>Company Drive Information</h2>
<table style="border-collapse: collapse; width: 100%; border: 1px solid black;">
    <thead>
    <tr>
      <th style="border: 1px solid black; padding: 8px;">Company Name</th>
      <th style="border: 1px solid black; padding: 8px;">Skills Required</th>
      <th style="border: 1px solid black; padding: 8px;">Job Title</th>
      <th style="border: 1px solid black; padding: 8px;">Actions(Apply/Reject)</th> <!-- Added a new column for actions -->
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let drive of companyDrives">
      <td style="border: 1px solid black; padding: 8px;">{{ drive.company }}</td>
      <td style="border: 1px solid black; padding: 8px;">{{ drive.skillsRequired }}</td>
      <td style="border: 1px solid black; padding: 8px;">{{ drive.jobTitle }}</td>
      <td>
        <!-- Buttons for Apply and Reject actions -->
        <button style="border: 1px solid black; padding: 8px;" (click)="applyToCompany(drive)">Apply</button> 
        <button style="border: 1px solid black; padding: 8px;" (click)="rejectCompany(drive)">Reject</button>
      </td>
    </tr>
  </tbody>
</table>
