import { HttpClient } from '@angular/common/http';
import { Component, EventEmitter, Output } from '@angular/core';
import { Router } from '@angular/router';
import {re}
@Component({
  selector: 'app-student-login',
  templateUrl: './student-login.component.html',
  styleUrls: ['./student-login.component.css']
})
export class StudentLoginComponent {
  username: string = '';
   password: string = '';
   errorMessage: string = '';
  emailOrMobile: string = '';
   constructor(private http: HttpClient, private router: Router
  ) {}
  currentComponent: string = '';

  @Output() loginSuccess = new EventEmitter<void>();

  isLoggedIn: boolean = false;

    onLoginSuccess() {
      this.isLoggedIn = true;
    }

  onSubmit() {
      console.log('Username:', this.emailOrMobile );
      console.log('Password:', this.password); 
      const loginData = {
        emailOrMobile: this.emailOrMobile,
        password: this.password
      };

      // Perform login HTTP request
      this.http.post(this.+'/login', loginData).subscribe(
        response => {
          console.log('Login successful', response);
          this.router.navigate(['/student-dashboard']); // Example redirect on success
        },
        error => {
          this.errorMessage = error.error.message;
        }
      );
    }


}
