import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { CompanyDriveService } from '../services/company-drive.service';

interface CompanyDrive {
  id: number;
  company: string;
  driveDate: string;
  location: string;
  jobTitle: string;
}

@Component({
  selector: 'app-drive',
  templateUrl: './drive.component.html',
  styleUrls: ['./drive.component.css']
})
export class DriveComponent implements OnInit {
  companyDrives: CompanyDrive[] = [];

  constructor(private http: HttpClient) {}

  ngOnInit() {
    this.fetchCompanyDrives();
  }

  fetchCompanyDrives() {
    this.http.get<CompanyDrive[]>('http://localhost:8080/company-drive')
      .subscribe((data) => {
        this.companyDrives = data;
      });
  }
}
<p>drive works!</p>


<!-- drive.component.html -->
<h2>Company Drive Information</h2>
<table>
  <thead>
    <tr>
      <th>Company Name</th>
      <th>Drive Date</th>
      <th>Location</th>
      <th>Description</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let drive of companyDrives">
      <td>{{ drive.company }}</td>
      <td>{{ drive.driveDate }}</td>
      <td>{{ drive.location }}</td>
      <td>{{ drive.description }}</td>
    </tr>
  </tbody>
</table>
