  <input type="text" [(ngModel)]="username" name="username" required />
    <br /><br />

  <!-- Mobile Number Field -->
  <input type="text" [(ngModel)]="mobileNumber" name="mobileNumber" required />
    <br /><br />
  <!-- Email Field -->
  <input type="email" [(ngModel)]="email" name="email" required />
    <br /><br />
  <!-- Company Name Field -->
  <input type="text" [(ngModel)]="companyName" name="companyName" required />

  <!-- Position Field -->
  <input type="text" [(ngModel)]="position" name="position" required />

  <button type="submit" [disabled]="recruiterForm.invalid">Register</button>
</form>




