import { Component } from '@angular/core';

@Component({
  selector: 'app-welcome',
  templateUrl: './welcome.component.html',
  styleUrls: ['./welcome.component.css']
})
export class WelcomeComponent {
  currentComponent: string = 'welcome'; // Initially show the welcome page

  // Function to show login form
  showLogin() {
    this.currentComponent = 'login';
  }

  // Function to show register form
  showRegister() {
    this.currentComponent = 'register';
  }

  // Function to handle login success and switch to student dashboard
  onLoginSuccess() {
    this.currentComponent = 'isLoggedIn';
  }

  // Function to handle logout and switch back to welcome page
  showWelcomePage() {
    this.currentComponent = 'welcome'; // Redirect back to welcome
  }
}
<!-- welcome-page.component.html -->
<h3>You are On Welcome Page!</h3>
<button class="btn-space" (click)="showLogin()">Login</button>
<button class="btn-space" (click)="showRegister()">Register</button>

<!-- Display the Login Component -->
<div *ngIf="currentComponent === 'login'">
  <!-- <app-student-login (loginSuccess)="onLoginSuccess()"></app-student-login> -->
  <app-student-login (loginSuccess)="onLoginSuccess()"></app-student-login>
</div>

<!-- Display the Register Component -->
<div *ngIf="currentComponent === 'register'">
    <!-- <app-recruiter-login></app-recruiter-login> -->
    <app-recruiter-login></app-recruiter-login>
</div>

<!-- Display the Student Dashboard after login -->
<div *ngIf="currentComponent === 'isLoggedIn'">
  <!-- <app-studentdashboard (logout)="showWelcomePage()"></app-studentdashboard> -->
  <app-student-dashboard (logout)="showWelcomePage()"></app-student-dashboard>
</div>
