import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { CompanyDriveService } from '../services/company-drive.service';

interface CompanyDrive {
  id: number;
  company: string;
  skillsRequired: string;
  location: string;
  jobTitle: string;
}

@Component({
  selector: 'app-drive',
  templateUrl: './drive.component.html',
  styleUrls: ['./drive.component.css']
})
export class DriveComponent implements OnInit {
  companyDrives: CompanyDrive[] = [];
  selectedFile: File | null = null;

  constructor(private http: HttpClient) {}

  ngOnInit() {
    this.fetchCompanyDrives();
  }

  fetchCompanyDrives() {
    // this.http.get<CompanyDrive[]>('http://localhost:8080/companydrives/getAllCompany')
    this.http.get<CompanyDrive[]>('http://localhost:8080/recruiter/getAllCompany')
      .subscribe((data) => {
        this.companyDrives = data;
      });
  }  


onFileSelected(event: Event) {
  const fileInput = event.target as HTMLInputElement;
  if (fileInput.files && fileInput.files.length > 0) {
    this.selectedFile = fileInput.files[0];
  }
}

applyToCompany(drive: any) {
  if (this.selectedFile) {
    const formData = new FormData();
    formData.append('resume', this.selectedFile);

    // Use the recruiter ID for application
    this.http.post(`http://localhost:8080/companydrives/apply/${drive.id}`, formData)
      .subscribe(response => {
        console.log('Application successful', response);
      }, error => {
        console.error('Error in application', error);
      });
  } else {
    alert('Please select a file before applying.');
  }
}


  rejectCompany(drive: any): void {
    // Logic to handle rejecting the company
    console.log(`Rejecting company: ${drive.company}`);
    // Add your backend integration here
  }
}
